<script>
  $(function() {
    $.cookie.defaults.path = "/";
    $.cookie.defaults.domain = "";
  
    if (true) {
      $.cookie.defaults.domain = ".sendgrid.com";
    }
  
    if (typeof SendGridTiara != "undefined") {
  
      var tiara = window.tiara = SendGridTiara.init({
        // Use dependency injection to simplify Tiara's dependency management
        jQuery: $,
        underscore: _,
  
        // Pass in the current user's MakoAPI token
        auth_token: $.cookie('mako_auth_token'),
  
        shouldLogoutOnAuthFail: true,
        logoutCallback: false ? function () {
          console.log('DIE logout callback activated');
          window.location = '/marketing_campaigns/auth/logout';
        } : undefined,
  
        sg_host: 'https://app.sendgrid.com'.replace('https://app.', 'https://'),
        api_host: 'https://api.sendgrid.com/v3/',
        makoHost: 'https://app.sendgrid.com',
  
        nlvxHost: "/marketing_campaigns",
        disable_impersonation: window.Nlvx.disableImpersonation,
        revealAllLinks: false
      });
  
      $.when(tiara.feature_toggles).always(function(){
        if(!Nlvx.flags.tourEnabled){
          $("a[href*='/marketing_campaigns/tour']").parent("li").remove()
        }
      });
  
    } else {
      $('a.logo img').css({width: 146})
    }
  
    // This is very hacky. NLVX includes StyleGuide, Tiara includes StyleGuide.
    // The JS that handles dropdowns gets included twice, so events get bound twice.
    // This ends up causing a tiara menu click to open and then immediately close,
    // because it's triggering 2 clicks. Those events are bound to the document, so for
    // the tiara nav, we're trapping the clicks so they don't bubble, and handling the show/hide
    // ourselves.
    $("[data-tiara-nav], header.tiara-headers").on("click","[data-dropdown-toggle]",function(e){
      var $e = $(e.currentTarget);
      var $target = $(e.target);
      var $dropdowns = $('[data-dropdown-toggle]');
  
      // Don't trap the event if someone is clicking on one of the dropdown links
      // so it does whatever it is supposed to do.
      if(!$target.is(".dropdown-link")){
        e.preventDefault();
      }
  
      // if a dropdown is already active, close all dropdown-menus
      if ($dropdowns.hasClass('is-active')) {
        // don't close when clicking a menu item like 'search'
        if ($target.closest('[data-dropdown-no-close]').length == 0) {
          $dropdowns.removeClass('is-active');
        }
      } else {
        // if they're clicking an inactive dropdown, open it
        $e.addClass('is-active');
      }
    });
  
    function addAuthTokenToAjaxRequest () {
      const makoAuthToken = $.cookie('mako_auth_token');
      $.ajaxPrefilter( function (options, originalOptions, jqXHR) {
        if (originalOptions.url && originalOptions.url.match(window.nlvxEnvironment.makoApiHost)) {
          jqXHR.setRequestHeader('AUTHORIZATION', 'token ' + makoAuthToken);
          if ($.cookie('sendgrid-impersonating-username')) jqXHR.setRequestHeader('On-behalf-of', $.cookie('sendgrid-impersonating-username'));
        }
      });
    }
  
    // This is currently used in the Rails edit_view.js pages of the content editor.  I removed all usage of the global var from the react app.
    // this is configured by a different means than the react app since this is rails land.  so they can get out of sync, beware
    // TODO remove this when legacy rails editor is deprecated
    function configureEnvironment() {
      window.nlvxEnvironment = {
        makoHost: 'https://app.sendgrid.com',
        makoApiHost: 'https://api.sendgrid.com/v3/'
      };
    }
  
    configureEnvironment();
    addAuthTokenToAjaxRequest();
  });
</script>


